import React from 'react';
import { Tab } from '@ya.praktikum/react-developer-burger-ui-components';
import style from './BurgerIngredients.module.css';
import ListIngredients from '../ListIngredients/ListIngredients';


const BurgerIngredients = ({ingredients}) => {
   const [current, setCurrent] = React.useState('one');

   const bun = ingredients.filter(elem => elem.type === 'bun');
   const main = ingredients.filter(elem => elem.type === 'main');
   const sauce = ingredients.filter(elem => elem.type === 'sauce');



   return (
      <div className={`${style.ing}`}>
         <nav className={style.tab}>

               <Tab value="one" active={current === 'one'} onClick={setCurrent}>
                  Булки
               </Tab>
               <Tab value="two" active={current === 'two'} onClick={setCurrent}>
                  Соусы
               </Tab>
               <Tab value="three" active={current === 'three'} onClick={setCurrent}>
                  Начинки
               </Tab>

         </nav>

         <div className={style.ingredients}>
            <ListIngredients ingredients={bun} title='Булки' />
            <ListIngredients ingredients={main} title='Начинки' />
            <ListIngredients ingredients={sauce} title='Соусы' />
         </div>
      </div>
   )
}

export default BurgerIngredients;
