import React from 'react';
import style from './BurgerConstructor.module.css'
import { DragIcon, ConstructorElement, CurrencyIcon, Button } from '@ya.praktikum/react-developer-burger-ui-components'
import img from '../../images/bun-02.png'

const BurgerConstructor = ({data}) => {


   return (
      <div className={style.constr}>
         <div className={style.components}>
            <div className='mr-6'>
               <ConstructorElement
                  type="top"
                  isLocked={true}
                  text="Краторная булка N-200i (верх)"
                  price={1255}
                  thumbnail={img}
               />
            </div>
            <ul className={style.elements}>
               {
                  data.map((item, index) => {
                     if (item.type === 'bun') {
                        return null
                     }
                     return (
                        <li key={index} className='m-4'>
                           <DragIcon type="primary" />
                           <ConstructorElement
                              text={item.name}
                              price={item.price}
                              thumbnail={item.image}
                           />
                        </li>
                     )
                  })
               }

            </ul>
            <div className='mr-4'>
               <ConstructorElement
                  type="bottom"
                  isLocked={true}
                  text="Краторная булка N-200i (низ)"
                  price={1255}
                  thumbnail={img}
               />
            </div>
         </div>

         <div className={style.info}>
            <div className={`${style.price} m-10`}>
               <p className="text text_type_digits-medium">10</p>

               <CurrencyIcon className={style.icon} type="primary" />
            </div>
            <Button type="primary" size="medium">
               Оформить заказ
            </Button>
         </div>
      </div>
   )
}

export default BurgerConstructor;
