import React from 'react';
import { Tab } from '@ya.praktikum/react-developer-burger-ui-components';
import style from './BurgerIngredients.module.css';
import ListIngredients from '../ListIngredients/ListIngredients';


const BurgerIngredients = ({ ingredients }) => {
   const [current, setCurrent] = React.useState('bun');
   const [modalIngredients, setModalIngredients] = React.useState()


   const bun = React.useMemo(() => ingredients.filter(elem => elem.type === 'bun'));
   const main = React.useMemo(() => ingredients.filter(elem => elem.type === 'main'));
   const sauce = React.useMemo(() => ingredients.filter(elem => elem.type === 'sauce'));



   return (
      <div className='mt-5 mr-10'>
         <nav className={style.tab}>
            <Tab value="bun" active={current === 'bun'} onClick={setCurrent}>
               Булки
            </Tab>
            <Tab value="main" active={current === 'main'} onClick={setCurrent}>
               Соусы
            </Tab>
            <Tab value="sauce" active={current === 'sauce'} onClick={setCurrent}>
               Начинки
            </Tab>
         </nav>

         <div className={style.ingredients}>
            <ListIngredients ingredients={bun} title='Булки' />
            <ListIngredients ingredients={main} title='Начинки' />
            <ListIngredients ingredients={sauce} title='Соусы' />
         </div>
      </div>
   )
}

export default BurgerIngredients;
