import React from 'react';
import PropTypes from 'prop-types';
import Ingredient from '../ingredient/Ingredient';
import style from './ListIngredients.module.css'

const ListIngredients = ({ingredients, title, clickIngredient, }) => {

   const dataToModal = () => {
      clickIngredient(element)
   }

   return (
      <>
         <div className='text text_type_main-medium mt-10 mb-6'>{title}</div>

         <div className={style.colomn}>
            {
               ingredients.map((element) => {
                  return (
                     <Ingredient ingredient={element} counter={1} onClick={dataToModal} key={element._id}/>
                  )
               })
            }
         </div>
      </>
   )
}

ListIngredients.propTypes ={
   title: PropTypes.string.isRequired,
   click: PropTypes.func
}

export default ListIngredients;
